import React, {useState, useEffect} from 'react';
import {StyleSheet, Text, View} from 'react-native';
import CountDown from 'react-native-countdown-component';
import {Header, Card, Button} from 'react-native-elements';
import StyledButton from './components/Button';

export default function App() {
  const [title, setTitle] = useState('TEST');
  const [addTimerState, setAddTimer] = useState(false);

  const toggleAddTimer = () => {
    console.log('hey')
    setAddTimer(!addTimerState)
  };

  return (
    <View style={styles.container}>
      <Header
        leftComponent={{icon: 'menu', color: '#fff'}}
        centerComponent={{text: 'interTimer', style: {color: '#fff'}}}
        rightComponent={{icon: 'home', color: '#fff'}}
        style={styles.header}
      />
      <CustomCard title={title}/>
      <StyledButton buttonStyle={'blue'} text={'Add Timer'} onClick={toggleAddTimer}/>
    </View>
  );
}


const CustomCard = (title) => {
  const text = title.title;
  return (
    <Card title={text}>
      <Timer/>
    </Card>
  )
};

const Timer = () => {
  return (
    <CountDown
      size={30}
      until={1000}
      onFinish={() => alert('Finished')}
      digitStyle={{backgroundColor: '#FFF', borderWidth: 2, borderColor: '#1CC625'}}
      digitTxtStyle={{color: '#1CC625'}}
      timeLabelStyle={{color: 'red', fontWeight: 'bold'}}
      separatorStyle={{color: '#1CC625'}}
      timeToShow={['M', 'S']}
      timeLabels={{m: null, s: null}}
      showSeparator
    />
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ffffff',
  }
});
