import React, {useState} from 'react';
import {StyleSheet, View} from 'react-native';
import delay from 'delay';
import CountDown from 'react-native-countdown-component';
import {Card} from "react-native-elements";

export default function MainTimer({timers}) {
  let [delayTime, setDelayTime] = useState(0);
  let [startTimer, nextTimer] = useState(true);

  const startNextTimer = () => {
    nextTimer(!startTimer)
  };

  const setDelayTimeHandler = (seconds) => {
    setDelayTime(seconds);
  }

  const TimerCard = ({title, seconds}) => {
    return (
      <Card title={title} containerStyle={styles.TimerCard}>
        <Timer seconds={seconds}/>
      </Card>
    )
  };

  const Timer = ({seconds}) => {
    return (
      <CountDown
        size={20}
        until={seconds}
        onFinish={() => startNextTimer()}
        digitStyle={{backgroundColor: '#ff0266', borderWidth: 2, borderColor: 'black'}}
        digitTxtStyle={{color: '#black'}}
        timeLabelStyle={{color: 'black', fontWeight: 'bold'}}
        separatorStyle={{color: 'black'}}
        timeToShow={['M', 'S']}
        timeLabels={{m: null, s: null}}
        showSeparator
      />
    )
  };

  return (
    <View>
      {timers.map((timer) => {
        delay(delayTime)
        const {order, title, seconds} = timer;
        if (startTimer) {

          return (
            <TimerCard key={order} title={title} seconds={seconds}/>
          )
        }
      })}
    </View>
  )
}

const styles = StyleSheet.create({
  TimerCard: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ff0266',
    borderColor: '#ff0266',
    borderRadius: '10px',
  },
})
