import React, {useState, useEffect} from 'react';
import {StyleSheet, Text, View} from 'react-native';
import CountDown from 'react-native-countdown-component';
import {Header, Card, Button} from 'react-native-elements';
import StyledButton from './components/Button';
import PlusIcon from './assets/icons8-plus.svg'

export default function App() {
  const [title, setTitle] = useState('TEST');
  const [addTimerState, setAddTimer] = useState(false);
  const toggleAddTimer = () => {
    setAddTimer(!addTimerState)
  };

  return (
    <View style={styles.container}>
      <TimerCard title={title}/>
      <StyledButton icon={PlusIcon}
                    buttonStyle={styles.addButtonStyle}
                    titleStyle={styles.addTitleStyle}
                    onClick={toggleAddTimer}/>
    </View>
  );
}

const TimerCard = (title) => {
  const text = title.title;
  return (
    <Card title={text} containerStyle={styles.TimerCard}>
      <Timer/>
    </Card>
  )
};

const Timer = () => {
  return (
    <CountDown
      size={20}
      until={1000}
      onFinish={() => alert('Finished')}
      digitStyle={{backgroundColor: '#ff0266', borderWidth: 2, borderColor: 'black'}}
      digitTxtStyle={{color: '#black'}}
      timeLabelStyle={{color: 'black', fontWeight: 'bold'}}
      separatorStyle={{color: 'black'}}
      timeToShow={['M', 'S']}
      timeLabels={{m: null, s: null}}
      showSeparator
    />
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#121212',
  },
  TimerCard: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ff0266',
    borderColor: '#ff0266',
    borderRadius: '10px',
  },
  addButtonStyle: {
    borderRadius: '50%',
    borderColor: 'black',
    backgroundColor: '#03dac6'
  },
  addTitleStyle: {
    padding: '5',
    color: 'black'
  }
});
